import React, {useState, useEffect} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import StarIcon from '@material-ui/icons/StarBorder';

import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import {PricingTable, PricingSlot, PricingDetail} from 'react-pricing-table';



const useStyles = makeStyles((theme) => ({
  '@global': {
    ul: {
      margin: 0,
      padding: 0,
      listStyle: 'none',
    },
  },
  heroContent: {
    padding: theme.spacing(8, 0, 6),
  },
  cardHeader: {
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[200] : theme.palette.grey[700],
  },
  cardPricing: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'baseline',
    marginBottom: theme.spacing(2),
  },button:{
      margin : 0,
  },button2:{
    padding: theme.spacing(1, 0.5 ,1),
  },button4:{
    padding: theme.spacing(1, 0.5 ,1),
    backgroundColor: 'green',
  },gridbox:{

      border: '2px solid #d3d3d3',
      borderRadius: '5px',
  },

}));


const tiers = [
  {title: '201',type:'2'},
  {title: '202',type:'4'},
  {title: '203',type:'4'},
  {title: '204',type:'4'},
  {title: '205',type:'4'},
  {title: '206',type:'4'},
  {title: '207',type:'4'},
  {title: '208',type:'4'},
  {title: '209',type:'4'},
  {title: '210',type:'4'},
  {title: '211',type:'4'},
  {title: '212',type:'4'},
  {title: '213',type:'4'},
  {title: '214',type:'4'},

];

export default function Pricing({GetPriceWon}) {
  const [selectPrice,setselectPrice] = useState(null);
  const classes = useStyles();
  const choose = (data)=> {
    GetPriceWon(data);
    setselectPrice(data);
  };
  return (
    <React.Fragment>
      <CssBaseline />
      <Container maxWidth="sm" component="main" className={classes.heroContent}>
        <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
          Room Type
        </Typography>
      </Container>

      <Container maxWidth="md" component="main">
        <Grid className={classes.gridbox} container spacing={5} alignItems="flex-end">

          {tiers.map((tier) => (
            <Grid  className={tier.type==='2'? classes.button2:classes.button4} key={tier.title}>
              <button className={classes.button}>
                {tier.title}
              </button>
            </Grid>
          ))}
        </Grid>
      </Container>
    </React.Fragment>
  );
}
