import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Grid from '@material-ui/core/Grid';
import Axios from 'axios';

// 사용자 주소 가져외기
const addresses = ['1 Material-UI Drive', 'Reactville', 'Anytown', '99999', 'USA'];
//사용자 카드 정보 가져오기\
const payments = [
  { name: 'Card type', detail: 'Visa' },
  { name: 'Card holder', detail: 'Mr John Smith' },
  { name: 'Card number', detail: 'xxxx-xxxx-xxxx-1234' },
  { name: 'Expiry date', detail: '04/2024' },
];

const useStyles = makeStyles((theme) => ({
  listItem: {
    padding: theme.spacing(1, 0),
  },
  total: {
    fontWeight: 700,
  },
  title: {
    marginTop: theme.spacing(2),
  },
}));

export default function Review({ CheckIn, CheckOut, Adult, Kid, PriceWon, RoomNumber, Getpricewon }) {
  const classes = useStyles();
  let Totalprice = 0;
  let products = [];
  let roominfo = [];
  let userinfo = [];
  useEffect(() => {
    Axios.post('http://localhost:5000/reservation/review', {
      RoomNumber: RoomNumber,
    }).then((response) => {
      console.log(response);
      roominfo.push(response.data.room);
      userinfo.push(response.data.user);
    });

  }, []);
  for (let i = 0; i < roominfo.length; i++) {
    // products.push({ name: roominfo[i].Room.Room_Num, desc: roominfo[i].Room.Room_Type, price: roominfo[i].Room.Price_won });
    // Totalprice += roominfo[i].Room.Price_won;
    console.log(roominfo);
  }
  Getpricewon(Totalprice);
  return (
    <React.Fragment>
      <Typography variant="h6" gutterBottom>
        예약 내용 확인
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            Check IN : {CheckIn}
          </Typography>
        </Grid>
        <Grid item container direction="column" xs={12} sm={6}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            Check OUT : {CheckOut}
          </Typography>
        </Grid>
      </Grid>

      <List disablePadding>
        {products.map((product) => (
          <ListItem className={classes.listItem} key={product.name}>
            <ListItemText primary={product.name + "호"} secondary={product.desc} />
            <Typography variant="body2">{product.price}</Typography>
          </ListItem>
        ))}

        <ListItem className={classes.listItem}>
          <ListItemText primary="Total" />
          <Typography variant="subtitle1" className={classes.total}>
            {Totalprice}
          </Typography>
        </ListItem>
      </List>

      <Grid container spacing={2}>
        <Grid item container direction="column" xs={12} sm={6}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            Payment details
          </Typography>
          <Grid container>
            {payments.map((payment) => (
              <React.Fragment>
                <Grid item xs={6}>
                  <Typography gutterBottom>{payment.name}</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography gutterBottom>{payment.detail}</Typography>
                </Grid>
              </React.Fragment>
            ))}
          </Grid>
        </Grid>
      </Grid>
    </React.Fragment>
  );
}
