import React ,{useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Grid from '@material-ui/core/Grid';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Link from '@material-ui/core/Link';
import Axios from 'axios';
import TextField from '@material-ui/core/TextField';
import SelecltDate from './Date';

const useStyles = makeStyles((theme) => ({

  appBar: {
    position: 'relative',
  },
  layout: {
    width: 'auto',
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 800,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  paper: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
      marginTop: theme.spacing(6),
      marginBottom: theme.spacing(6),
      padding: theme.spacing(3),
    },
  },
  buttons: {
    display: 'flex',
    justifyContent: 'flex-end',
    padding: 10


  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },
  space: {
      marginTop: theme.spacing(2),
  },
  listItem: {
    padding: theme.spacing(1, 0),
  },
  total: {
    fontWeight: 700,
  },
  title: {
    marginTop: theme.spacing(2),
  },
}));


export default function SingleRoom() {
  const classes = useStyles();

  return (
    <>
  <React.Fragment>
      <CssBaseline />
      <main className={classes.layout}>
        <Paper className={classes.paper}>
        <Typography component="h1" variant="h4" align="center">
            예약 조회
          </Typography>
      <Grid className={classes.title} container spacing={2}>
        <Grid item xs={16} sm={2}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            예약 번호
          </Typography>
        </Grid>
        <Grid item xs={16} sm={2}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            <margin className={classes.margin1}>객실 타입 </margin>
          </Typography>
        </Grid>
        <Grid item xs={16} sm={2}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            <margin className={classes.margin2}>호수</margin>
          </Typography>
        </Grid>
        <Grid item xs={16} sm={3}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            <margin className={classes.margin3}>체크인</margin>
          </Typography>
        </Grid>
        <Grid item xs={16} sm={2}>
          <Typography variant="h6" gutterBottom className={classes.title}>
            <margin className={classes.margin4} >체크아웃</margin>
          </Typography>
        </Grid>
      </Grid>
      </Paper>

      </main>
    </React.Fragment>
    </>
  );
}
