import React, { useState, useEffect } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Axios from 'axios'
import Genderset from './Gender';
import SelecltConuntry from './SelectCountry';
import SelecltDate from './Date';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
  Axios.defaults.withCredentials = true; // for cookie
  const classes = useStyles();
  const [First_Name, setFirst_Name] = useState(null);
  const [Last_Name, setLast_Name] = useState(null);
  const [E_Mail, setE_Mail] = useState(null);
  const [Login_PW, setLogin_PW] = useState(null);
  const [Birthday, setBirthday] = useState(null);
  const [Login_ID, setLogin_ID] = useState(null);
  const [Login_PWcheck, setLogin_PWcheck] = useState(null);
  const [Phone_Number, setPhone_Number] = useState(null);
  const [Zip, setZip] = useState(null);
  const [Apt_Num, setApt_Num] = useState(null);
  const [Nationality, setNationality] = useState(null);
  const [Fax, setFax] = useState('000-000-0000');
  const [Gender, setGender] = useState('NO');
  const [checkidok, setcheckidok] = useState(false);
  const getBirthday = (Birthday) => setBirthday(Birthday);
  const getNationality = (Nationality) => setNationality(Nationality);
  const getgender = (Gender) => setGender(Gender);
  const submitInfo = () => {
    if (Apt_Num != null && Gender != null && Zip != null && Nationality != null && Phone_Number != null && Birthday != null && Login_PW != null && Login_PWcheck == Login_PW && Login_ID != null && First_Name != null && Last_Name != null && E_Mail != null) {
      Axios.post('http://localhost:5000/signup', {
        First_Name: First_Name,
        Last_Name: Last_Name,
        E_Mail: E_Mail,
        Login_PW: Login_PW,
        Apt_Num: Apt_Num,
        Zip: Zip,
        Phone_Number: Phone_Number,
        Login_ID: Login_ID,
        Birthday: Birthday,
        Fax: Fax,
        Nationality: Nationality.label,
        Gender: Gender
      }).then((res) => {
        if (res.data.result) {
          document.location.href = '/login'
          alert('회원가입 성공');
        }
      });

    } else {
      alert("데이터입력이 틀렸습니다.");
    }
  };
  const checkID = () => {
    console.log("여기까지 작동하나요?")
    Axios.post('http://localhost:5000/signup/checkid', {
      Login_ID: Login_ID
    }).then((res) => {
      console.log(res.data.isok);
      setcheckidok(res.data.isok);
    })
  }
  useEffect(() => {
    Axios.get('http://localhost:5000/login').then((response) => {
      // if(response.data.loggedIn == true){

      // }
      console.log(response); //login 되면 console loggedin 값 true
    }); //get : refresh 하면 login in or not 

  }, []);

  return (

    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          회원가입
        </Typography>
        <form className={classes.form} noValiBirthday>
          <Grid container spacing={2}>
            <Grid item xs={9}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="Login_ID"
                label="ID"
                name="Login_ID"
                autoComplete="Login_ID"
                onChange={(e) => {
                  setLogin_ID(e.target.value);
                }}
              />
            </Grid>
            <Grid item xs={3}>
              <Button
                fullWidth
                variant="contained"
                color="primary"
                className={classes.submit}
                onClick={checkID}
              >
                중복확인
            </Button>
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="Login_PW"
                label="PW"
                type="password"
                id="Login_PW"
                autoComplete="current-Login_PW"
                onChange={(e) => {
                  setLogin_PW(e.target.value);
                }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="Login_PW_Check"
                label="PW Check"
                type="password"
                id="Login_PW_Check"
                autoComplete="current-Login_PW"
                onChange={(e) => {
                  setLogin_PWcheck(e.target.value);
                }}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                autoComplete="fname"
                name="First_Name"
                variant="outlined"
                required
                fullWidth
                id="First_Name"
                label="First Name"
                autoFocus
                onChange={(e) => {
                  setFirst_Name(e.target.value);
                }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="Last_Name"
                label="Last Name"
                name="Last_Name"
                autoComplete="lname"
                onChange={(e) => {
                  setLast_Name(e.target.value);
                }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="E_Mail"
                label="E-Mail"
                name="E_Mail"
