import React, { useState, useEffect } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import StarIcon from '@material-ui/icons/StarBorder';

import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { PricingTable, PricingSlot, PricingDetail } from 'react-pricing-table';

import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles((theme) => ({
    '@global': {
        ul: {
            margin: 0,
            padding: 0,
            listStyle: 'none',
        },
    }, button: {
        margin: 0,
    }, buttongrid: {
        padding: theme.spacing(1, 0.5, 1),
    }, button4: {
        backgroundColor: 'green',
        padding: ".285rem .815rem",
        border: "1px solid teal",
        borderRadius: ".25rem",
        fontSize: "1rem",
        lineHeight: 1.5,

    }, button2: {
        backgroundColor: 'red',
        padding: ".285rem .815rem",
        border: "1px solid teal",
        borderRadius: ".25rem",
        fontSize: "1rem",
        lineHeight: 1.5,
    }, button1: {
        backgroundColor: 'blue',
        padding: ".285rem .815rem",
        border: "1px solid teal",
        borderRadius: ".25rem",
        fontSize: "1rem",
        lineHeight: 1.5,
    }, gridbox: {

        border: '2px solid #d3d3d3',
        borderRadius: '5px',
    },

}));


const tiers = [
    { title: '201', type: '2', floor: 2 },
    { title: '202', type: '2', floor: 2 },
    { title: '203', type: '2', floor: 2 },
    { title: '204', type: '2', floor: 2 },
    { title: '205', type: '2', floor: 2 },
    { title: '206', type: '2', floor: 2 },
    { title: '207', type: '2', floor: 2 },
    { title: '208', type: '2', floor: 2 },
    { title: '209', type: '2', floor: 2 },
    { title: '210', type: '2', floor: 2 },
    { title: '211', type: '4', floor: 2 },
    { title: '212', type: '4', floor: 2 },
    { title: '213', type: '4', floor: 2 },
    { title: '214', type: '4', floor: 2 },


    //3
    { title: '301', type: '2', floor: 3 },
    { title: '302', type: '2', floor: 3 },
    { title: '303', type: '2', floor: 3 },
    { title: '304', type: '2', floor: 3 },
    { title: '305', type: '2', floor: 3 },
    { title: '306', type: '2', floor: 3 },
    { title: '307', type: '2', floor: 3 },
    { title: '308', type: '2', floor: 3 },
    { title: '309', type: '2', floor: 3 },
    { title: '310', type: '2', floor: 3 },
    { title: '311', type: '4', floor: 3 },
    { title: '312', type: '4', floor: 3 },
    { title: '313', type: '4', floor: 3 },
    { title: '314', type: '4', floor: 3 },


];

export default function Selectroom({ Getroomnuber, UserSelectRoomType }) {
    const [selectPrice, setselectPrice] = useState(null);
    const [floor, setfloor] = useState(2);
    const classes = useStyles();

    const handleChange = (event) => {
        setfloor(event.target.value);
    };
    const selectroombutton = (data) => {
        Getroomnuber(data);
        alert(data);

    };
    function getbutton(tier, floor) {
        if (floor == tier.floor) {
            switch (tier.type) {
                case '2':
                    return (
                        <Grid className={classes.buttongrid} key={tier.title}>
                            <button className={classes.button2} onClick={() => selectroombutton(tier.title)}>
                                {tier.title}
                            </button>
                        </Grid>
                    )
                case '4':
                    return (
                        <Grid className={classes.buttongrid} key={tier.title}>
                            <button className={classes.button4} onClick={() => selectroombutton(tier.title)}>
                                {tier.title}
                            </button>
                        </Grid>
                    )
            }
        }
    }

    return (
        <React.Fragment>
            <CssBaseline />
            <Container maxWidth="sm" component="main" className={classes.heroContent}>
                <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
                    Select Room
                </Typography>
            </Container>

            <Container >
                <Grid spacing={5} >
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={floor}
                        onChange={ handleChange}
                    >
                        <MenuItem value={2}>2</MenuItem>
                        <MenuItem value={3}>3</MenuItem>
                        <MenuItem value={4}>4</MenuItem>
                        <MenuItem value={5}>5</MenuItem>
                        <MenuItem value={6}>6</MenuItem>
                    </Select>
                </Grid>
            </Container>

            <Container maxWidth="md" component="main">

                <Grid className={classes.gridbox} container spacing={5} alignItems="flex-end">
                    {tiers.map((tier) => (
                        getbutton(tier, floor)
                    ))}
                </Grid>
            </Container>
        </React.Fragment>
    );
}
